css:
.letter-wrap { display: grid; grid-template-columns: 1fr 1fr; gap: 24px; align-items: start; }
.panel { padding: 16px; border: 0px solid #000; border-radius: 10px; }
.panel h2 { margin: 0 0 8px 0; font-size: 16px; }
.meta { font-size: 12px; color: #555; margin-bottom: 12px; }
.rtl { direction: rtl; text-align: right; }
.ltr { direction: ltr; text-align: left; }
.muted { color: #666; }
.tag { display:inline-block; padding: 2px 8px; border:1px solid #ccc; border-radius: 999px; font-size: 11px; }
.two-col-title { display:flex; justify-content:space-between; align-items:center; margin-bottom:12px;}

html:
{% if doc.docstatus != 1 %}
<div style="text-align:center; padding:40px; color:#b00; font-size:18px;">
  {{ _("⚠️ Cannot print draft document. Please submit first.") }}
</div>
{% else %}

   {% set emp = frappe.get_doc('Employee', doc.employee) %}
{% set tmpl = frappe.get_doc('Employee Letter Template', doc.letter_template) %}

{# تنظيف النص الإنجليزي والعربي قبل الحكم #}
{% set en_raw = tmpl.template_text_english or '' %}
{% set ar_raw = tmpl.template_text_arabic or '' %}

{% set en_text = (en_raw | striptags | replace('&nbsp;', '') | replace('\u00a0','') | trim) %}
{% set ar_text = (ar_raw | striptags | replace('&nbsp;', '') | replace('\u00a0','') | trim) %}

{% set has_en = en_text %}
{% set has_ar = ar_text %}

{% if has_en and has_ar %}
  <div class="letter-wrap" style="grid-template-columns: 1fr 1fr;">
    <div class="panel ltr">
      {{ frappe.render_template(en_raw, {"doc": doc, "employee": emp}) | safe }}
    </div>
    <div class="panel rtl">
      {{ frappe.render_template(ar_raw, {"doc": doc, "employee": emp}) | safe }}
    </div>
  </div>

{% elif has_ar %}
  <div class="letter-wrap" style="grid-template-columns: 1fr;">
    <div class="panel rtl">
      {{ frappe.render_template(ar_raw, {"doc": doc, "employee": emp}) | safe }}
    </div>
  </div>

{% elif has_en %}
  <div class="letter-wrap" style="grid-template-columns: 1fr;">
    <div class="panel ltr">
      {{ frappe.render_template(en_raw, {"doc": doc, "employee": emp}) | safe }}
    </div>
  </div>

{% else %}
  <div style="padding:20px; color:#a00; border:1px dashed #a00;">
    ⚠️ لا توجد نصوص قالب لهذه الرسالة.
  </div>
{% endif %}


    
{% endif %}
