css:
@page {
    size: 6cm 4cm;
    margin: 1mm 1mm 1mm 1mm;
}

@media print {
    .pagebreak {
        clear: both;
        page-break-after: always;
    }
}

.print-format {
    padding: 0;
    position: absolute;
    top: 1mm;
    left : 1mm;
    right : 1mm;
    bottom : 1mm;
    border: 0 solid green;
}

.print-format td{
    padding: 0px !important;
}

.print-format th{
    padding: 0px !important;
    background-color: #adacac;
}

table {
    width:100%;
}

table, th, td {
  border: 0px solid black;
  border-collapse: collapse;
  padding: 0;
  padding-top: 0;
  padding-bottom: 0;
  padding-left: 0;
  padding-right: 0;
  text-align:center;
}
