html:
{% set doc = frappe.get_doc("Product Development", doc.name) %}
{% set version_doc = frappe.get_doc(doc.type, doc.version) %}

<style>
    @page {
        size: A4;
        margin: 20mm;
        @bottom-center {
            content: "Page " counter(page) " of " counter(pages);
            font-size: 9pt;
            color: #444;
        }
    }

    body {
        font-family: Arial, sans-serif;
        font-size: 9pt;
    }

    h2 {
        text-align: center;
        margin-bottom: 10px;
    }

    h4 {
        margin-top: 8px;
        margin-bottom: 4px;
    }

    table {
        width: 100%;
        border-collapse: collapse;
        table-layout: fixed;
    }

    th, td {
        border: 1px solid #444;
        padding: 5px 6px;
        font-size: 9pt;
        white-space: nowrap;
        overflow: hidden;
        text-overflow: ellipsis;
        vertical-align: middle;
    }

    th {
        background-color: #f5f5f5;
        text-align: center;
    }

    td {
        text-align: left;
    }

    .header-table {
        margin-bottom: 0;
    }

    .header-table td {
        width: 25%;
    }

    .remark-row td {
        border-top: none;
        padding-top: 6px;
        padding-bottom: 6px;
        font-style: italic;
        color: #333;
        white-space: normal;
        text-align: left;
    }

    /* جدول المكونات الصلبة والسائلة */
    .solid-liquid-table th:nth-child(1), .solid-liquid-table td:nth-child(1) { width: 20%;}
    .solid-liquid-table th:nth-child(2), .solid-liquid-table td:nth-child(2) { width: 40%;}
    .solid-liquid-table th:nth-child(3), .solid-liquid-table td:nth-child(3) { width: 20%;}
    .solid-liquid-table th:nth-child(4), .solid-liquid-table td:nth-child(3) { width: 20%;}

    /* جدول المواد الخام */
    .material-table th:nth-child(1), .material-table td:nth-child(1) { width: 15%; }
    .material-table th:nth-child(2), .material-table td:nth-child(2) { width: 35%; }
    .material-table th:nth-child(3), .material-table td:nth-child(3) { width: 8%; text-align: right; }
    .material-table th:nth-child(4), .material-table td:nth-child(4) { width: 7%; text-align: center; }
    .material-table th:nth-child(5), .material-table td:nth-child(5) { width: 15%; }
    .material-table th:nth-child(6), .material-table td:nth-child(6) { width: 10%; text-align: center; }
    .material-table th:nth-child(7), .material-table td:nth-child(7) { width: 10%; text-align: center; }
</style>

<h2>Raw Material Requirement</h2>

<!-- 🧾 جدول معلومات المنتج -->
<table class="header-table">
    <tbody>
        <!-- الصف الأول -->
        <tr>
            <td><b>Post Date:</b> {{ doc.post_date or '' }}</td>
            <td><b>Type:</b> {{ doc.type or '' }}</td>
            <td><b>Size:</b> {{ doc.size or '' }}</td>
            <td><b>Weight (gram):</b> {{ doc.weight_gram or '' }}</td>
        </tr>

        <!-- الصف الثاني -->
        <tr>
            <td><b>Item Code:</b> {{ doc.item_code or '' }}</td>
            <td><b>Item Name:</b> {{ doc.item_name or '' }}</td>
            <td><b>Version:</b> {{ doc.version or '' }}</td>
            <td><b>QTY:</b> {{ doc.qty or '' }}</td>
        </tr>

        {% if doc.remark %}
        <!-- الصف الثالث (ملاحظات) -->
        <tr class="remark-row">
            <td colspan="4"><strong>Remark:</strong> {{ doc.remark }}</td>
        </tr>
        {% endif %}
    </tbody>
</table>

{% if doc.type == "Product Proposal" %}
<br>
<h4>Solid / Liquid Components</h4>
<table class="solid-liquid-table">
    <thead>
        <tr>
            <th>Component Type</th>
            <th>Component Name</th>
            <th>Size</th>
            <th>Weight</th>
        </tr>
    </thead>
    <tbody>
        {% for comp in version_doc.pp_solid_liquid %}
        <tr>
            <td>{{ comp.component_type or '' }}</td>
            <td>{{ comp.component_name or '' }}</td>
            <td>{{ comp.size or '' }}</td>
            <td>{{ comp.weight or '' }}</td>
        </tr>
        {% endfor %}
        {% if not version_doc.pp_solid_liquid %}
        <tr><td colspan="3" style="text-align:center;">No components found</td></tr>
        {% endif %}
    </tbody>
</table>
{% endif %}

<br>
<h4>Raw Materials / Components</h4>

<table class="material-table">
    <thead>
        <tr>
            <th>Item Code</th>
            <th>Item Name</th>
            <th>QTY</th>
            <th>UOM</th>
            <th>Operation</th>
            <th>Temp.</th>
            <th>Duration (min)</th>
        </tr>
    </thead>
    <tbody>
    {% if doc.type == "BOM" %}
        {% set bom_qty = version_doc.quantity or 1 %}
        {% for item in version_doc.items %}
            {% set new_qty = (item.qty / bom_qty) * doc.qty %}
            <tr>
                <td>{{ item.item_code }}</td>
                <td>{{ item.item_name }}</td>
                <td style="text-align:right;">{{ "{:.3f}".format(new_qty) }}</td>
                <td style="text-align:center;">{{ item.uom }}</td>
                <td>{{ item.operation or "" }}</td>
                <td style="text-align:center;"></td>
                <td style="text-align:center;"></td>
            </tr>
        {% endfor %}
    {% elif doc.type == "Product Proposal" %}
        {% for item in version_doc.pp_items %}
            {% set new_qty = (item.qty / version_doc.quantity) * doc.qty %}
            <tr> 
                <td>{{ item.item_code }}</td>
                <td>{{ item.item_name }}</td>
                <td style="text-align:right;">{{ "{:.3f}".format(new_qty) }}</td>
                <td style="text-align:center;">{{ item.uom or "" }}</td>
                <td>{{ item.operation or "" }}</td>
                <td style="text-align:center;">{{ item.temperature or "" }}</td>
                <td style="text-align:center;">{{ item.duration or "" }}</td>
            </tr>
            {% if item.notes %}
            <tr class="remark-row">
                <td colspan="7"><strong>Note:</strong> {{ item.notes }}</td>
            </tr>
            {% endif %}
        {% endfor %}
    {% else %}
        <tr><td colspan="7" style="text-align:center;">No data found</td></tr>
    {% endif %}
    </tbody>
</table>
